mapper.insert:
INSERT INTO
	member
	(
		user_id
		, user_pwd
		, name
		, phone
		, zip_code
		, address
		, sub_address
	)
VALUES
	(
		#{user_id}
		, #{user_pwd}
		, #{name}
		, #{phone}
		, #{zip_code}
		, #{address}
		, #{sub_address}
	)

mapper.select:
/* 회원 아이디 비번 check*/
SELECT
	*
FROM
	member
WHERE
	user_id = #{user_id}
	and
	user_pwd = #{user_pwd}
